import React from 'react';
import styled from 'styled-components';
import PropTpyes from 'prop-types';
import {List, Button, Card} from 'antd';
import { StopOutlined } from '@ant-design/icons';


const ListWrapper = styled(List)`
    margin-bottom: 20px;
`

const loadStyle = styled.div`
    text-align: center;
    margin: 10px 0;
`

const ListItemStyle = styled(List.Item)`
    margin-top: 20px;
`

const FollowList = ({header, data}) => {
    return (
        <ListWrapper header={<div>{header}</div>} grid={{gutter:4, xs: 2, md: 3}} size="small" loadMore={<loadStyle>
            <Button>
                더보기
            </Button>
        </loadStyle>} bordered dataSource={data} renderItem={(item)=>
        (
            <ListItemStyle>
                <Card action={[<StopOutlined key="stop"/>]}>
                    <Card.Meta description={item.nickname}/>
                </Card>
            </ListItemStyle>
        )}>
            
        </ListWrapper>
    );
}

FollowList.propTypes = {
    header: PropTpyes.string.isRequired,
    data: PropTpyes.array.isRequired,
}

export default FollowList;
