import { all, fork, put, takeLatest, delay } from 'redux-saga/effects';
import axios from "axios";

import { ADD_POST_REQUEST, POST_ADD_SUCCESS, POST_ADD_FAILURE } from '../reducers/post';

function postAddAPI(data){
    return axios.post('/api/post', data);
}

function* postAdd(action){
    try{
        // const result = yield call(postAddAPI, action.data);
        yield delay(1000);
        yield put({
            type: POST_ADD_SUCCESS,
        });
    }catch(err){
        yield put({
            type: POST_ADD_FAILURE,
            data: err.response.data
        })
    }
}

function* watchAddPost(){
    yield takeLatest(ADD_POST_REQUEST, postAdd);
}

export default function* postSage(){
    yield all([
        fork(watchAddPost),
    ])
}
