import React from 'react';
import PropTypes from 'prop-types';
import {Card, Popover, ButtonGroup, Button} from 'antd';
import {RetweetOulined, HeartOutlined, MessageOutlined, EllipsisOutlined} from '@ant-design/icons';
import { useSelector } from 'react-redux';

const PostCard = ({post}) =>{
    // const {me} = useSelector((state) => state.post);
    const id = useSelector((state) => state.post.me?.id);
    // const id = me && me.id; 구
    // const id = me?.id; 신

    return (
        <div>
            <Card cover={post.Images[0] && <PostImages images={post.Images}/>}
                actions={[
                    <RetweetOulined key="retweet"/>,
                    <HeartOutlined key="heart"/>,
                    <MessageOutlined key="comment"/>,
                    <Popover key="more" content={(
                        <ButtonGroup>
                            {id && post.User.id === id &&  
                            <>
                                <Button> 수정</Button>
                                <Button type="danger"> 삭제</Button>
                            </>
                        }
                            <Button>신고</Button>
                        </ButtonGroup>
                    )}>
                        <EllipsisOutlined/>
                    </Popover>,
                ]}
            >
                <Image />
                <Content />
                <Buttons></Buttons>
            </Card>
            <CommentForm />
            <Comments/>
        </div>
    );
};

PostCard.propTypes = {
    // post: PropTypes.object.isRequired,
    post: PropTypes.shapy({
        id: PropTypes.number,
        User: PropTypes.object,
        content: PropTypes.string,
        createdAt: PropTypes.object,
        Comments: PropTypes.arrayOf(PropTypes.object),
        Images: PropTypes.arrayOf(PropTypes.object)
    }).isRequired,
}

export default PostCard;
